mapper.select:
select sci.id, sci.course_date, sci.start_time, sci.end_time, sci.class_id, sci.course_id, cai.name as className, ci.name as courseName, ci.address, ci.duration, ci.people_num, ci.credit, si.name as staffName, si.images as staffImages, ti.name as tieName, mi.name as majorName from schedule_class_info sci left join class_info cai on (cai.id = sci.class_id) left join course_info ci on (ci.id = sci.course_id) left join staff_info si on (si.id = ci.staff_id) left join tie_info ti on (ti.id = ci.tie_id) left join major_info mi on (mi.id = ci.major_id) where 1 = 1
AND ci.id = #{classId}
order by sci.course_date desc

select count(1)
from schedule_class_info
where 1 = 1

    AND DATE_FORMAT( schedule_class_info.course_date, '%Y' ) = #{year}


    AND DATE_FORMAT( schedule_class_info.course_date, '%m' ) = #{month}

select count(1)
from schedule_elective_info
where 1 = 1

    AND DATE_FORMAT( schedule_elective_info.course_date, '%Y' ) = #{year}


    AND DATE_FORMAT( schedule_elective_info.course_date, '%m' ) = #{month}

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN schedule_class_info oi ON ( DATE_FORMAT( oi.course_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' )
AND oi.user_id = #{userId}
) GROUP BY days ORDER BY days ASC

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN schedule_elective_info oi ON ( DATE_FORMAT( oi.course_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' )
AND oi.send_user = #{userId}
) GROUP BY days ORDER BY days ASC
