mapper.select:
select si.id, si.code, si.name, si.sex, si.birthday, si.native_address, si.id_card, si.images, si.diploma, si.school_name, si.address, si.email, si.phone, si.create_date, si.del_flag, si.dept_id, si.position_id, si.tie_id, si.major_id, di.dept_name, poi.name as positionName, ti.name as tieName, mi.name as majorName from staff_info si left join dept_info di on (di.id = si.dept_id) left join position_info poi on (poi.id = si.position_id) left join tie_info ti on (ti.id = si.tie_id) left join major_info mi on (mi.id = si.major_id) where 1 = 1 and si.del_flag = '0'
AND si.code LIKE CONCAT('%',#{staffInfo.code},'%')
AND si.name LIKE CONCAT('%',#{staffInfo.name},'%')
AND si.dept_id = #{staffInfo.deptId}
AND si.position_id = #{staffInfo.positionId}
order by si.create_date desc

select si.id, si.code, si.name, si.sex, si.birthday, si.native_address, si.id_card, si.images, si.diploma, si.school_name, si.address, si.email, si.phone, si.create_date, si.del_flag, si.dept_id, si.position_id, di.dept_name, poi.name as positionName from staff_info si left join dept_info di on (di.id = si.dept_id) left join position_info poi on (poi.id = si.position_id) where 1 = 1 and si.del_flag = '0' and si.id in
#{item}
order by si.create_date desc
